import React, { useState } from 'react';
import { FaPlus, FaSearch } from 'react-icons/fa';
import './Inventory.css';

const Inventory = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedMedicine, setSelectedMedicine] = useState(null);

  // Sample inventory data
  const inventoryData = [
    {
      id: 1,
      name: "Lipitor (Atorvastatin)",
      category: "Cardiology",
      currentStock: 2500,
      unit: "tablets",
      manufacturer: "Pfizer",
      expiryDate: "2024-06-15",
      location: "Storage A-12",
      status: "In Stock",
      reorderPoint: 500
    },
    {
      id: 2,
      name: "Metformin HCl",
      category: "Endocrinology",
      currentStock: 180,
      unit: "bottles",
      manufacturer: "Novartis",
      expiryDate: "2024-04-30",
      location: "Storage B-05",
      status: "Low Stock",
      reorderPoint: 200
    },
    {
      id: 3,
      name: "Amoxicillin",
      category: "Antibiotics",
      currentStock: 350,
      unit: "bottles",
      manufacturer: "GSK",
      expiryDate: "2024-05-15",
      location: "Storage C-03",
      status: "In Stock",
      reorderPoint: 100
    }
  ];

  const inventoryStats = {
    total: inventoryData.length,
    expiringSoon: inventoryData.filter(item => {
      const daysUntilExpiry = Math.ceil((new Date(item.expiryDate) - new Date()) / (1000 * 60 * 60 * 24));
      return daysUntilExpiry < 30;
    }).length,
    lowStock: inventoryData.filter(item => item.currentStock < item.reorderPoint).length
  };

  return (
    <div className="inventory-page">
      {/* Header Section */}
      <div className="inventory-header">
        <div className="header-left">
          <h1>Inventory Management</h1>
        </div>
        <button className="add-btn">
          <FaPlus /> Add Item
        </button>
      </div>

      {/* Search Section */}
      <div className="search-section">
        <div className="search-bar">
          <FaSearch />
          <input
            type="text"
            placeholder="Search by name, category, or location..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
      </div>

      {/* Inventory Table */}
      <div className="inventory-table">
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Category</th>
              <th>Stock</th>
              <th>Expiry Date</th>
              <th>Location</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {inventoryData
              .filter(item => 
                item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                item.category.toLowerCase().includes(searchTerm.toLowerCase()) ||
                item.location.toLowerCase().includes(searchTerm.toLowerCase())
              )
              .map(item => (
                <tr key={item.id}>
                  <td className="medicine-name">{item.name}</td>
                  <td>{item.category}</td>
                  <td>
                    <span className={item.currentStock < item.reorderPoint ? 'low-stock' : ''}>
                      {item.currentStock} {item.unit}
                    </span>
                  </td>
                  <td>{item.expiryDate}</td>
                  <td>{item.location}</td>
                  <td>
                    <span className={`status-badge ${item.status.toLowerCase().replace(' ', '-')}`}>
                      {item.status}
                    </span>
                  </td>
                  <td>
                    <button 
                      className="view-btn"
                      onClick={() => setSelectedMedicine(item)}
                    >
                      View Details
                    </button>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>

      {/* Medicine Detail Modal */}
      {selectedMedicine && (
        <MedicineModal
          medicine={selectedMedicine}
          onClose={() => setSelectedMedicine(null)}
        />
      )}
    </div>
  );
};

export default Inventory;
