import React, { useState, useEffect } from 'react';
import { FaBoxes, FaHistory, FaBrain, FaSync, FaMapMarkerAlt, FaChartLine, FaTimes, FaTemperatureHigh, FaCalendarAlt, FaTruckMoving, FaBoxOpen, FaChartBar, FaMicroscope, FaRegChartBar } from 'react-icons/fa';
import './Orders.css';
import MedicineDetailModal from '../components/MedicineDetailModal';

const generateSampleOrders = (count) => {
  const medicines = [
    'Amoxicillin 500mg', 'Lisinopril 10mg', 'Metformin 850mg', 'Omeprazole 20mg',
    'Sertraline 50mg', 'Atorvastatin 40mg', 'Levothyroxine 100mcg', 'Amlodipine 5mg',
    'Gabapentin 300mg', 'Metoprolol 25mg', 'Escitalopram 10mg', 'Losartan 50mg',
    'Vitamin D3 5000IU', 'Pantoprazole 40mg', 'Citalopram 20mg'
  ];

  const suppliers = [
    'PharmaCo Ltd', 'MediSupply Inc', 'GlobalMed', 'HealthCare Distributors',
    'PharmaWorld', 'MedExpress', 'BioPharm Solutions', 'LifeScience Labs'
  ];

  const statuses = ['Processing', 'Shipped', 'In Transit', 'Delivered', 'Pending'];

  return Array.from({ length: count }, (_, index) => ({
    orderId: `ORD-2024-${(index + 1).toString().padStart(3, '0')}`,
    medicine: medicines[Math.floor(Math.random() * medicines.length)],
    quantity: Math.floor(Math.random() * 1000) + 100,
    orderDate: new Date(2024, 0, Math.floor(Math.random() * 90) + 1).toISOString().split('T')[0],
    expectedDelivery: new Date(2024, 1, Math.floor(Math.random() * 90) + 1).toISOString().split('T')[0],
    status: statuses[Math.floor(Math.random() * statuses.length)],
    supplier: suppliers[Math.floor(Math.random() * suppliers.length)],
    amount: Math.floor(Math.random() * 5000) + 500,
    priority: Math.random() > 0.7 ? 'High' : Math.random() > 0.4 ? 'Medium' : 'Low',
    tracking: {
      current: 'In Transit',
      location: 'Distribution Center',
      updates: [
        { date: '2024-02-15', status: 'Order Placed', location: 'Supplier Warehouse' },
        { date: '2024-02-16', status: 'Processing', location: 'Packaging Center' },
        { date: '2024-02-18', status: 'Shipped', location: 'Distribution Hub' },
        { date: '2024-02-20', status: 'In Transit', location: 'Local Facility' }
      ]
    }
  }));
};

const generateHealthTrends = () => {
  return {
    localOutbreaks: [
      {
        disease: 'Seasonal Flu',
        intensity: 'High',
        affectedAreas: ['Urban Centers', 'Schools'],
        trend: 'Increasing',
        relatedMedicines: ['Oseltamivir', 'Antipyretics'],
        preventiveMeasures: ['Vaccination', 'Hand Hygiene']
      },
      {
        disease: 'Allergic Rhinitis',
        intensity: 'Moderate',
        affectedAreas: ['Suburban Areas', 'Parks'],
        trend: 'Stable',
        relatedMedicines: ['Antihistamines', 'Nasal Sprays'],
        preventiveMeasures: ['Air Purification', 'Pollen Avoidance']
      }
    ],
    weatherImpact: {
      temperature: '28Â°C',
      humidity: '65%',
      airQuality: 'Moderate',
      pollenCount: 'High',
      healthRisks: ['Respiratory Issues', 'Heat Stress']
    },
    demographicTrends: {
      ageGroups: {
        'Under 18': { commonConditions: ['Viral Infections', 'Allergies'] },
        '18-40': { commonConditions: ['Stress-related', 'Digestive Issues'] },
        '40-60': { commonConditions: ['Hypertension', 'Diabetes'] },
        'Over 60': { commonConditions: ['Arthritis', 'Cardiovascular'] }
      }
    }
  };
};

// OrderDetailModal Component
const OrderDetailModal = ({ order, onClose }) => {
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Order Details</h2>
          <button className="close-btn" onClick={onClose}>
            <FaTimes />
          </button>
        </div>
        <div className="modal-body">
          <div className="order-info">
            <h3>Order Information</h3>
            <div className="info-grid">
              <div className="info-item">
                <label>Order ID</label>
                <span>{order.orderId}</span>
              </div>
              <div className="info-item">
                <label>Medicine</label>
                <span>{order.medicine}</span>
              </div>
              <div className="info-item">
                <label>Quantity</label>
                <span>{order.quantity}</span>
              </div>
              <div className="info-item">
                <label>Amount</label>
                <span>${order.amount}</span>
              </div>
              <div className="info-item">
                <label>Supplier</label>
                <span>{order.supplier}</span>
              </div>
              <div className="info-item">
                <label>Status</label>
                <span className={`status-badge ${order.status.toLowerCase()}`}>
                  {order.status}
                </span>
              </div>
            </div>
          </div>
          
          <div className="tracking-timeline">
            <h3>Tracking Information</h3>
            <div className="timeline">
              {order.tracking.updates.map((update, index) => (
                <div key={index} className="timeline-item">
                  <div className="timeline-marker"></div>
                  <div className="timeline-content">
                    <h4>{update.status}</h4>
                    <p>{update.location}</p>
                    <span className="timeline-date">{update.date}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const Orders = () => {
  const [activeTab, setActiveTab] = useState('active');
  const [healthTrends, setHealthTrends] = useState(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [activeOrders, setActiveOrders] = useState([]);
  const [orderHistory, setOrderHistory] = useState([]);
  const [aiInsights, setAiInsights] = useState({});
  const [selectedTimeframe, setSelectedTimeframe] = useState('month');
  const [selectedMedicine, setSelectedMedicine] = useState(null);

  // AI Predictions and Insights
  const aiPredictions = [
    {
      id: 1,
      medicine: "Paracetamol 500mg",
      predictedDemand: 1500,
      confidence: 89,
      factors: [
        { type: "Seasonal", impact: "High", detail: "Monsoon season approaching - fever cases expected to rise" },
        { type: "Historical", impact: "Medium", detail: "20% increase in demand during similar period last year" },
        { type: "Environmental", impact: "High", detail: "Predicted rainfall in next 2 weeks" }
      ],
      recommendedAction: "Increase stock by 40%"
    },
    {
      id: 2,
      medicine: "Azithromycin 250mg",
      predictedDemand: 800,
      confidence: 92,
      factors: [
        { type: "Disease Outbreak", impact: "High", detail: "Recent respiratory infections in nearby areas" },
        { type: "Weather", impact: "Medium", detail: "Dropping temperatures increase risk of infections" },
        { type: "Population", impact: "Low", detail: "Increased patient registration in local clinics" }
      ],
      recommendedAction: "Place order for 1000 units"
    },
    // Add more AI predictions
  ];

  // Filter orders based on status
  const activeOrdersFiltered = activeOrders.filter(order => 
    ['Processing', 'Shipped', 'In Transit'].includes(order.status)
  );

  const orderHistoryFiltered = activeOrders.filter(order => 
    ['Delivered', 'Completed'].includes(order.status)
  );

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    setHealthTrends(generateHealthTrends());
  };

  const refreshTrends = async () => {
    setIsRefreshing(true);
    await new Promise(resolve => setTimeout(resolve, 1500));
    setHealthTrends(generateHealthTrends());
    setIsRefreshing(false);
  };

  // Generate sample active orders
  useEffect(() => {
    const statuses = ['Processing', 'In Transit', 'Out for Delivery'];
    const medicines = [
      'Amoxicillin', 'Ibuprofen', 'Paracetamol', 'Omeprazole', 'Metformin',
      'Lisinopril', 'Amlodipine', 'Metoprolol', 'Simvastatin', 'Sertraline'
    ];
    const suppliers = ['PharmaCorp', 'MediSupply', 'HealthDirect', 'GlobalMeds', 'CareMeds'];

    const generateOrders = (count, isActive) => {
      return Array.from({ length: count }, (_, i) => {
        const today = new Date();
        const orderDate = new Date(today - Math.random() * 30 * 24 * 60 * 60 * 1000);
        const deliveryDays = Math.floor(Math.random() * 7) + 3;
        const expectedDelivery = new Date(orderDate.getTime() + deliveryDays * 24 * 60 * 60 * 1000);
        
        return {
          id: isActive ? `AO${i + 1}` : `HO${i + 1}`,
          medicine: medicines[Math.floor(Math.random() * medicines.length)],
          quantity: Math.floor(Math.random() * 1000) + 100,
          amount: Math.floor(Math.random() * 10000) + 1000,
          orderDate: orderDate.toISOString().split('T')[0],
          expectedDelivery: expectedDelivery.toISOString().split('T')[0],
          status: isActive ? 
            statuses[Math.floor(Math.random() * statuses.length)] : 
            'Delivered',
          supplier: suppliers[Math.floor(Math.random() * suppliers.length)],
          priority: Math.random() < 0.3 ? 'High' : Math.random() < 0.6 ? 'Medium' : 'Low',
          trackingNumber: `TRK${Math.random().toString(36).substr(2, 8).toUpperCase()}`
        };
      });
    };

    setActiveOrders(generateOrders(50, true));
    setOrderHistory(generateOrders(100, false));
  }, []);

  // Generate AI insights
  useEffect(() => {
    const generateAIInsights = () => {
      return {
        demandPredictions: {
          title: 'Demand Forecast Analysis',
          predictions: [
            {
              medicine: 'Amoxicillin',
              predictedDemand: Math.floor(Math.random() * 1000) + 500,
              confidence: 92.5,
              trend: 'increasing',
              factors: [
                { name: 'Seasonal Pattern', impact: 0.35 },
                { name: 'Historical Usage', impact: 0.45 },
                { name: 'Market Trends', impact: 0.20 }
              ]
            }
          ]
        },
        stockOptimization: {
          title: 'Inventory Optimization Insights',
          recommendations: [
            {
              type: 'Reorder Point Adjustment',
              details: 'Based on lead time analysis and demand variability',
              confidence: 88.7,
              impact: 'High',
              potentialSavings: '$12,500'
            }
          ]
        },
        anomalyDetection: {
          title: 'Supply Chain Anomaly Detection',
          findings: []
        },
        supplierAnalysis: {
          title: 'Supplier Performance Analytics',
          metrics: []
        }
      };
    };

    setAiInsights(generateAIInsights());
  }, [selectedTimeframe]);

  // Enhanced medicine data generation
  const generateMedicineDetails = (medicineName) => {
    const manufacturers = {
      'Amoxicillin': 'GlaxoSmithKline',
      'Ibuprofen': 'Pfizer',
      'Paracetamol': 'Johnson & Johnson',
      'Omeprazole': 'AstraZeneca',
      'Metformin': 'Novartis',
      'Lisinopril': 'Merck',
      'Amlodipine': 'Pfizer',
      'Metoprolol': 'AstraZeneca',
      'Simvastatin': 'Merck',
      'Sertraline': 'Pfizer'
    };

    const today = new Date();
    const expiryDate = new Date(today.getTime() + (Math.floor(Math.random() * 365) + 1) * 24 * 60 * 60 * 1000);
    const daysUntilExpiry = Math.floor((expiryDate - today) / (1000 * 60 * 60 * 24));

    return {
      name: medicineName,
      currentStock: Math.floor(Math.random() * 1000) + 100,
      daysUntilExpiry,
      reorderPoint: Math.floor(Math.random() * 200) + 50,
      manufacturer: manufacturers[medicineName],
      serialNumber: `SN${Math.random().toString(36).substr(2, 8).toUpperCase()}`,
      storageLocation: `Storage ${String.fromCharCode(65 + Math.floor(Math.random() * 6))}-${Math.floor(Math.random() * 10) + 1}`,
      expiryDate: expiryDate.toISOString().split('T')[0],
      category: ['Antibiotics', 'Painkillers', 'Cardiovascular', 'Antidiabetic'][Math.floor(Math.random() * 4)],
      stockStatus: daysUntilExpiry < 30 ? 'Low Stock' : 'In Stock'
    };
  };

  // Enhanced AI insights with more complex models
  const generateEnhancedAIInsights = () => {
    const currentDate = new Date();
    const currentMonth = currentDate.getMonth();
    const seasons = ['Winter', 'Spring', 'Summer', 'Fall'];
    const currentSeason = seasons[Math.floor(currentMonth / 3) % 4];

    return {
      demandPredictions: {
        title: 'Advanced Demand Forecast Analysis',
        methodology: 'Using LSTM Neural Network with Multi-variable Analysis',
        predictions: [
          {
            medicine: 'Amoxicillin',
            predictedDemand: {
              nextMonth: Math.floor(Math.random() * 500) + 1000,
              nextQuarter: Math.floor(Math.random() * 1500) + 3000,
              nextYear: Math.floor(Math.random() * 5000) + 12000
            },
            confidence: 94.5,
            trend: 'increasing',
          factors: [
              { name: 'Seasonal Pattern', impact: 0.35, trend: 'positive' },
              { name: 'Historical Usage', impact: 0.45, trend: 'stable' },
              { name: 'Market Trends', impact: 0.20, trend: 'positive' },
              { name: 'Population Health Index', impact: 0.15, trend: 'stable' }
            ],
            seasonalityIndex: calculateSeasonalityIndex(currentSeason)
          }
        ]
      },
      supplyChainOptimization: {
        title: 'Supply Chain Intelligence',
        analysis: {
          leadTimeOptimization: {
            currentAverage: '5.3 days',
            recommendedActions: [
              {
                action: 'Supplier Consolidation',
                potentialImprovement: '18%',
                implementation: 'Medium',
                roi: '3.2x'
              }
            ],
            bottlenecks: identifyBottlenecks()
          },
          inventoryOptimization: calculateInventoryOptimization(),
          qualityMetrics: generateQualityMetrics()
        }
      },
      marketIntelligence: {
        title: 'Market Intelligence & Pricing Analytics',
        insights: generateMarketInsights(),
        priceElasticity: calculatePriceElasticity(),
        competitorAnalysis: generateCompetitorAnalysis()
      },
      riskAssessment: {
        title: 'Predictive Risk Assessment',
        factors: generateRiskFactors(),
        mitigationStrategies: generateMitigationStrategies()
      }
    };
  };

  // Helper functions for AI insights
  const calculateSeasonalityIndex = (season) => {
    const baseIndex = Math.random() * 0.4 + 0.8; // 0.8 to 1.2
    const seasonalFactors = {
      'Winter': 1.2,
      'Spring': 1.1,
      'Summer': 0.9,
      'Fall': 1.0
    };
    return (baseIndex * seasonalFactors[season]).toFixed(2);
  };

  const identifyBottlenecks = () => {
    return [
      {
        stage: 'Order Processing',
        impact: 'Medium',
        frequency: '12%',
        resolution: 'Process Automation'
      },
      {
        stage: 'Quality Control',
        impact: 'High',
        frequency: '8%',
        resolution: 'Additional QC Staff'
      }
    ];
  };

  const calculateInventoryOptimization = () => {
    return {
      safetyStock: {
        current: Math.floor(Math.random() * 200) + 300,
        recommended: Math.floor(Math.random() * 200) + 250,
        savings: Math.floor(Math.random() * 5000) + 5000
      },
      reorderPoints: {
        adjustments: [
          {
            medicine: 'Amoxicillin',
            current: 100,
            recommended: 85,
            reason: 'Reduced lead time variability'
          }
        ]
      },
      storageOptimization: {
        utilizationRate: (Math.random() * 10 + 85).toFixed(1) + '%',
        recommendations: ['Zone reorganization', 'Batch consolidation']
      }
    };
  };

  const generateQualityMetrics = () => {
    return {
      defectRate: (Math.random() * 0.5).toFixed(3) + '%',
      supplierScorecard: {
        reliability: (Math.random() * 10 + 90).toFixed(1) + '%',
        qualityConsistency: (Math.random() * 5 + 95).toFixed(1) + '%',
        documentationAccuracy: (Math.random() * 5 + 94).toFixed(1) + '%'
      }
    };
  };

  const generateMarketInsights = () => {
    return {
      marketShare: {
        value: (Math.random() * 15 + 25).toFixed(1) + '%',
        trend: 'increasing',
        competitors: [
          { name: 'Competitor A', share: '30%' },
          { name: 'Competitor B', share: '25%' },
          { name: 'Others', share: '20%' }
        ]
      },
      demandDrivers: [
        {
          factor: 'Aging Population',
          impact: 'High',
          trend: 'Increasing',
          opportunity: 'Expand geriatric medicine line'
        }
      ]
    };
  };

  const calculatePriceElasticity = () => {
    return {
      elasticityIndex: -1.2,
      priceRecommendations: [
        {
          medicine: 'Amoxicillin',
          currentPrice: 45.99,
          recommendedPrice: 42.99,
          expectedImpact: '+8% volume'
        }
      ]
    };
  };

  const generateCompetitorAnalysis = () => {
    return {
      competitors: [
        {
          name: 'Competitor A',
          strengths: ['Price', 'Distribution'],
          weaknesses: ['Quality', 'Range'],
          marketStrategy: 'Aggressive pricing'
        }
      ],
      opportunities: [
        'Premium segment expansion',
        'Digital channel development'
      ]
    };
  };

  const generateRiskFactors = () => {
    return [
      {
        category: 'Supply Chain',
        probability: 'Medium',
        impact: 'High',
        indicators: ['Supplier concentration', 'Geographic risks']
      },
      {
        category: 'Regulatory',
        probability: 'Low',
        impact: 'Very High',
        indicators: ['Policy changes', 'Compliance requirements']
      }
    ];
  };

  const generateMitigationStrategies = () => {
    return [
      {
        risk: 'Supply Chain Disruption',
        strategy: 'Supplier Diversification',
        implementation: 'Q3 2024',
        cost: 'Medium',
        effectiveness: 'High'
      }
    ];
  };

  // Handle medicine click
  const handleMedicineClick = (medicineName) => {
    const medicineDetails = generateMedicineDetails(medicineName);
    setSelectedMedicine(medicineDetails);
  };

  // Update your table rows to include click handlers
  const renderOrderRow = (order) => (
    <tr key={order.id}>
      <td>{order.id}</td>
      <td>
        <button 
          className="medicine-link"
          onClick={() => handleMedicineClick(order.medicine)}
        >
          {order.medicine}
        </button>
      </td>
      <td>{order.quantity}</td>
      <td>{order.amount.toLocaleString()}</td>
      <td>{order.orderDate}</td>
      <td>{order.expectedDelivery || 'N/A'}</td>
      <td>
        <span className={`status-badge ${order.status.toLowerCase()}`}>
          {order.status}
        </span>
      </td>
      <td>{order.supplier}</td>
      <td>
        <span className={`priority-badge ${order.priority.toLowerCase()}`}>
          {order.priority}
        </span>
      </td>
      <td>{order.trackingNumber}</td>
    </tr>
  );

  const renderOrderTable = (ordersList) => (
    <table className="orders-table">
      <thead>
        <tr>
          <th>Order ID</th>
          <th>Medicine</th>
          <th>Quantity</th>
          <th>Amount</th>
          <th>Order Date</th>
          <th>Expected Delivery</th>
          <th>Status</th>
          <th>Supplier</th>
          <th>Priority</th>
          <th>Tracking</th>
        </tr>
      </thead>
      <tbody>
        {ordersList.map(order => renderOrderRow(order))}
      </tbody>
    </table>
  );

  const renderAIInsights = () => {
    const insights = generateEnhancedAIInsights();
    
    return (
    <div className="ai-insights-container">
        {/* Demand Predictions */}
        <div className="insight-card">
          <h3><FaChartLine /> {insights.demandPredictions.title}</h3>
          <div className="methodology">
            <FaMicroscope /> {insights.demandPredictions.methodology}
          </div>
          {insights.demandPredictions.predictions.map((pred, idx) => (
            <div key={idx} className="prediction-detail">
              <h4>{pred.medicine}</h4>
              <div className="forecast-periods">
                <div className="period">
                  <span>Next Month</span>
                  <strong>{pred.predictedDemand.nextMonth}</strong>
                </div>
                <div className="period">
                  <span>Next Quarter</span>
                  <strong>{pred.predictedDemand.nextQuarter}</strong>
                </div>
                <div className="period">
                  <span>Next Year</span>
                  <strong>{pred.predictedDemand.nextYear}</strong>
                </div>
              </div>
              <div className="confidence">
                Confidence: {pred.confidence}%
                <div className="confidence-bar" style={{width: `${pred.confidence}%`}} />
              </div>
              <div className="factors-grid">
                {pred.factors.map((factor, fidx) => (
                  <div key={fidx} className="factor-card">
                    <div className="factor-header">
                      <span>{factor.name}</span>
                      <span className={`trend-indicator ${factor.trend}`} />
                    </div>
                    <div className="impact-bar" style={{width: `${factor.impact * 100}%`}} />
                    <span className="impact-value">{(factor.impact * 100).toFixed(0)}%</span>
                  </div>
                ))}
        </div>
      </div>
          ))}
        </div>

        {/* Supply Chain Optimization */}
        <div className="insight-card">
          <h3><FaTruckMoving /> {insights.supplyChainOptimization.title}</h3>
          <div className="optimization-grid">
            <div className="lead-time">
              <h4>Lead Time Optimization</h4>
              <div className="current-average">
                Current Average: {insights.supplyChainOptimization.analysis.leadTimeOptimization.currentAverage}
              </div>
              <div className="bottlenecks">
                <h5>Identified Bottlenecks</h5>
                {insights.supplyChainOptimization.analysis.leadTimeOptimization.bottlenecks.map((bottleneck, idx) => (
                  <div key={idx} className="bottleneck-card">
                    <span className="stage">{bottleneck.stage}</span>
                    <span className={`impact ${bottleneck.impact.toLowerCase()}`}>
                      {bottleneck.impact}
                    </span>
                    <span className="frequency">{bottleneck.frequency}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>

        {/* Market Intelligence */}
        <div className="insight-card">
          <h3><FaRegChartBar /> {insights.marketIntelligence.title}</h3>
          <div className="market-share">
            <h4>Market Share Analysis</h4>
            <div className="share-chart">
              {insights.marketIntelligence.marketShare.competitors.map((competitor, idx) => (
                <div key={idx} className="share-bar">
                  <div className="bar" style={{width: competitor.share}} />
                  <span>{competitor.name}: {competitor.share}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Risk Assessment */}
        <div className="insight-card">
          <h3><FaBrain /> {insights.riskAssessment.title}</h3>
          <div className="risk-grid">
            {insights.riskAssessment.factors.map((risk, idx) => (
              <div key={idx} className="risk-card">
                <h4>{risk.category}</h4>
                <div className={`risk-indicator ${risk.probability.toLowerCase()}`}>
                  {risk.probability} Probability
                </div>
                <div className={`impact-indicator ${risk.impact.toLowerCase().replace(' ', '-')}`}>
                  {risk.impact} Impact
                </div>
                <div className="indicators">
                  {risk.indicators.map((indicator, iidx) => (
                    <span key={iidx} className="indicator">{indicator}</span>
                  ))}
                </div>
              </div>
            ))}
        </div>
      </div>
    </div>
  );
  };

  const handleUpdateStock = (medicineId, updateDetails) => {
    // Update the stock in your state
    setActiveOrders(prevOrders => 
      prevOrders.map(order => {
        if (order.medicine === selectedMedicine.name) {
          const newStock = updateDetails.action === 'add' 
            ? selectedMedicine.currentStock + updateDetails.quantity
            : selectedMedicine.currentStock - updateDetails.quantity;
          
          return {
            ...order,
            currentStock: newStock
          };
        }
        return order;
      })
    );

    // Show success message
    alert(`Stock successfully ${updateDetails.action}ed by ${updateDetails.quantity} units`);
  };

  const handlePlaceOrder = (medicineId, orderDetails) => {
    // Add new order to active orders
    const newOrder = {
      id: `AO${activeOrders.length + 1}`,
      medicine: selectedMedicine.name,
      quantity: orderDetails.quantity,
      amount: orderDetails.quantity * 10, // Sample price calculation
      orderDate: new Date().toISOString().split('T')[0],
      expectedDelivery: orderDetails.expectedDelivery.split('T')[0],
      status: 'Processing',
      supplier: orderDetails.supplier,
      priority: orderDetails.priority,
      trackingNumber: `TRK${Math.random().toString(36).substr(2, 8).toUpperCase()}`
    };

    setActiveOrders(prevOrders => [...prevOrders, newOrder]);
    
    // Show success message
    alert('New order placed successfully');
  };

  return (
    <div className="orders-page">
      <div className="orders-header">
        <h1>Orders Management</h1>
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'active' ? 'active' : ''}`}
            onClick={() => setActiveTab('active')}
          >
            Active Orders ({activeOrders.length})
          </button>
          <button 
            className={`tab ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => setActiveTab('history')}
          >
            Order History ({orderHistory.length})
          </button>
          <button 
            className={`tab ${activeTab === 'insights' ? 'active' : ''}`}
            onClick={() => setActiveTab('insights')}
          >
            AI Insights
          </button>
        </div>
      </div>

      <div className="orders-content">
        {activeTab === 'active' && (
          <div className="active-orders">
            {renderOrderTable(activeOrdersFiltered)}
          </div>
        )}

        {activeTab === 'history' && (
          <div className="order-history">
            <table>
              <thead>
                <tr>
                  <th>Order ID</th>
                  <th>Medicine</th>
                  <th>Quantity</th>
                  <th>Amount</th>
                  <th>Order Date</th>
                  <th>Delivery Date</th>
                  <th>Status</th>
                  <th>Supplier</th>
                  <th>Batch No.</th>
                  <th>Quality Check</th>
                  <th>Storage Conditions</th>
                </tr>
              </thead>
              <tbody>
                {orderHistory.map(order => (
                  <tr key={order.id}>
                    <td>{order.id}</td>
                    <td>
                      <button 
                        className="medicine-link"
                        onClick={() => handleMedicineClick(order.medicine)}
                      >
                        {order.medicine}
                      </button>
                    </td>
                    <td>{order.quantity}</td>
                    <td>${order.amount.toLocaleString()}</td>
                    <td>{order.orderDate}</td>
                    <td>{order.deliveryDate}</td>
                    <td>
                      <span className="status-badge delivered">
                        {order.status}
                      </span>
                    </td>
                    <td>{order.supplier}</td>
                    <td>{order.batchNumber}</td>
                    <td>
                      <span className={`quality-badge ${order.qualityCheck.toLowerCase().replace(' ', '-')}`}>
                        {order.qualityCheck}
                      </span>
                    </td>
                    <td>
                      <span className="storage-conditions">
                        {order.temperature}Â°C / {order.humidity}%
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {activeTab === 'insights' && renderAIInsights()}
      </div>

      {selectedOrder && (
        <OrderDetailModal 
          order={selectedOrder} 
          onClose={() => setSelectedOrder(null)}
        />
      )}

      {selectedMedicine && (
        <MedicineDetailModal 
          medicine={selectedMedicine}
          onClose={() => setSelectedMedicine(null)}
          onUpdateStock={handleUpdateStock}
          onPlaceOrder={handlePlaceOrder}
        />
      )}
    </div>
  );
};

export default Orders;
