import React, { useState, useEffect } from 'react';
import { FaBoxes, FaHistory, FaBrain, FaSync, FaMapMarkerAlt, FaChartLine } from 'react-icons/fa';
import { motion, AnimatePresence } from 'framer-motion';
import './Orders.css';

const generateSampleOrders = (count) => {
  const medicines = [
    'Amoxicillin 500mg', 'Lisinopril 10mg', 'Metformin 850mg', 'Omeprazole 20mg',
    'Sertraline 50mg', 'Atorvastatin 40mg', 'Levothyroxine 100mcg', 'Amlodipine 5mg',
    'Gabapentin 300mg', 'Metoprolol 25mg', 'Escitalopram 10mg', 'Losartan 50mg',
    'Vitamin D3 5000IU', 'Pantoprazole 40mg', 'Citalopram 20mg'
  ];

  const suppliers = [
    'PharmaCo Ltd', 'MediSupply Inc', 'GlobalMed', 'HealthCare Distributors',
    'PharmaWorld', 'MedExpress', 'BioPharm Solutions', 'LifeScience Labs'
  ];

  const statuses = ['Processing', 'Shipped', 'In Transit', 'Delivered', 'Pending'];

  return Array.from({ length: count }, (_, index) => ({
    orderId: `ORD-2024-${(index + 1).toString().padStart(3, '0')}`,
    medicine: medicines[Math.floor(Math.random() * medicines.length)],
    quantity: Math.floor(Math.random() * 1000) + 100,
    orderDate: new Date(2024, 0, Math.floor(Math.random() * 90) + 1).toISOString().split('T')[0],
    expectedDelivery: new Date(2024, 1, Math.floor(Math.random() * 90) + 1).toISOString().split('T')[0],
    status: statuses[Math.floor(Math.random() * statuses.length)],
    supplier: suppliers[Math.floor(Math.random() * suppliers.length)],
    amount: Math.floor(Math.random() * 5000) + 500,
    priority: Math.random() > 0.7 ? 'High' : Math.random() > 0.4 ? 'Medium' : 'Low',
    tracking: {
      current: 'In Transit',
      location: 'Distribution Center',
      updates: [
        { date: '2024-02-15', status: 'Order Placed', location: 'Supplier Warehouse' },
        { date: '2024-02-16', status: 'Processing', location: 'Packaging Center' },
        { date: '2024-02-18', status: 'Shipped', location: 'Distribution Hub' },
        { date: '2024-02-20', status: 'In Transit', location: 'Local Facility' }
      ]
    }
  }));
};

const generateHealthTrends = () => {
  return {
    localOutbreaks: [
      {
        disease: 'Seasonal Flu',
        intensity: 'High',
        affectedAreas: ['Urban Centers', 'Schools'],
        trend: 'Increasing',
        relatedMedicines: ['Oseltamivir', 'Antipyretics'],
        preventiveMeasures: ['Vaccination', 'Hand Hygiene']
      },
      {
        disease: 'Allergic Rhinitis',
        intensity: 'Moderate',
        affectedAreas: ['Suburban Areas', 'Parks'],
        trend: 'Stable',
        relatedMedicines: ['Antihistamines', 'Nasal Sprays'],
        preventiveMeasures: ['Air Purification', 'Pollen Avoidance']
      }
    ],
    weatherImpact: {
      temperature: '28Â°C',
      humidity: '65%',
      airQuality: 'Moderate',
      pollenCount: 'High',
      healthRisks: ['Respiratory Issues', 'Heat Stress']
    },
    demographicTrends: {
      ageGroups: {
        'Under 18': { commonConditions: ['Viral Infections', 'Allergies'] },
        '18-40': { commonConditions: ['Stress-related', 'Digestive Issues'] },
        '40-60': { commonConditions: ['Hypertension', 'Diabetes'] },
        'Over 60': { commonConditions: ['Arthritis', 'Cardiovascular'] }
      }
    }
  };
};

const Orders = () => {
  const [activeTab, setActiveTab] = useState('active');
  const [orders, setOrders] = useState([]);
  const [healthTrends, setHealthTrends] = useState(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    setOrders(generateSampleOrders(50));
    setHealthTrends(generateHealthTrends());
  };

  const refreshTrends = async () => {
    setIsRefreshing(true);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1500));
    setHealthTrends(generateHealthTrends());
    setIsRefreshing(false);
  };

  const renderActiveOrders = () => (
    <div className="orders-table">
      <table>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Medicine</th>
            <th>Quantity</th>
            <th>Order Date</th>
            <th>Expected Delivery</th>
            <th>Status</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.orderId}>
              <td>{order.orderId}</td>
              <td>{order.medicine}</td>
              <td>{order.quantity}</td>
              <td>{order.orderDate}</td>
              <td>{order.expectedDelivery}</td>
              <td>
                <span className={`status-badge ${order.status.toLowerCase().replace(' ', '-')}`}>
                  {order.status}
                </span>
              </td>
              <td>
                <button className="track-btn">Track Order</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const renderPredictions = () => (
    <div className="predictions-container">
      {healthTrends && (
        <>
          <div className="prediction-category">
            <h3>Local Health Trends</h3>
            <div className="prediction-cards">
              {healthTrends.localOutbreaks.map((outbreak, index) => (
                <div key={index} className="prediction-card">
                  <h4>{outbreak.disease}</h4>
                  <div className="prediction-details">
                    <p><strong>Intensity:</strong> {outbreak.intensity}</p>
                    <p><strong>Affected Areas:</strong> {outbreak.affectedAreas.join(', ')}</p>
                    <p><strong>Trend:</strong> {outbreak.trend}</p>
                    <p><strong>Related Medicines:</strong> {outbreak.relatedMedicines.join(', ')}</p>
                    <p><strong>Preventive Measures:</strong> {outbreak.preventiveMeasures.join(', ')}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
          <div className="prediction-category">
            <h3>Weather Impact</h3>
            <div className="prediction-cards">
              {Object.entries(healthTrends.weatherImpact).map(([key, value]) => (
                <div key={key} className="prediction-card">
                  <h4>{key}</h4>
                  <div className="prediction-details">
                    <p>{value}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
          <div className="prediction-category">
            <h3>Demographic Trends</h3>
            <div className="prediction-cards">
              {Object.entries(healthTrends.demographicTrends.ageGroups).map(([ageGroup, details]) => (
                <div key={ageGroup} className="prediction-card">
                  <h4>{ageGroup}</h4>
                  <div className="prediction-details">
                    <p><strong>Common Conditions:</strong> {details.commonConditions.join(', ')}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </>
      )}
    </div>
  );

  return (
    <div className="orders-modern-container">
      <div className="orders-header">
        <h1>Order Management & Analytics</h1>
        <div className="header-actions">
          <button className="refresh-btn" onClick={refreshTrends} disabled={isRefreshing}>
            <FaSync className={isRefreshing ? 'spinning' : ''} /> Refresh Trends
          </button>
        </div>
      </div>

      <div className="orders-navigation">
        <button 
          className={`nav-btn ${activeTab === 'active' ? 'active' : ''}`}
          onClick={() => setActiveTab('active')}
        >
          <FaBoxes /> Active Orders
        </button>
        <button 
          className={`nav-btn ${activeTab === 'history' ? 'active' : ''}`}
          onClick={() => setActiveTab('history')}
        >
          <FaHistory /> Order History
        </button>
        <button 
          className={`nav-btn ${activeTab === 'predictions' ? 'active' : ''}`}
          onClick={() => setActiveTab('predictions')}
        >
          <FaBrain /> AI Insights
        </button>
      </div>

      <AnimatePresence mode="wait">
        <motion.div
          key={activeTab}
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -20 }}
          transition={{ duration: 0.3 }}
          className="tab-content"
        >
          {activeTab === 'active' && renderActiveOrders()}
          {activeTab === 'predictions' && renderPredictions()}
          {activeTab === 'history' && <div>Order History Content</div>}
        </motion.div>
      </AnimatePresence>

      {selectedOrder && (
        <OrderDetailModal 
          order={selectedOrder} 
          onClose={() => setSelectedOrder(null)}
        />
      )}
    </div>
  );
};

export default Orders;
