import React, { useState, useEffect } from 'react';
import { FaBoxes, FaHistory, FaBrain, FaSync, FaMapMarkerAlt, FaChartLine, FaTimes, FaTemperatureHigh, FaCalendarAlt, FaTruckMoving, FaBoxOpen, FaChartBar } from 'react-icons/fa';
import './Orders.css';

const generateSampleOrders = (count) => {
  const medicines = [
    'Amoxicillin 500mg', 'Lisinopril 10mg', 'Metformin 850mg', 'Omeprazole 20mg',
    'Sertraline 50mg', 'Atorvastatin 40mg', 'Levothyroxine 100mcg', 'Amlodipine 5mg',
    'Gabapentin 300mg', 'Metoprolol 25mg', 'Escitalopram 10mg', 'Losartan 50mg',
    'Vitamin D3 5000IU', 'Pantoprazole 40mg', 'Citalopram 20mg'
  ];

  const suppliers = [
    'PharmaCo Ltd', 'MediSupply Inc', 'GlobalMed', 'HealthCare Distributors',
    'PharmaWorld', 'MedExpress', 'BioPharm Solutions', 'LifeScience Labs'
  ];

  const statuses = ['Processing', 'Shipped', 'In Transit', 'Delivered', 'Pending'];

  return Array.from({ length: count }, (_, index) => ({
    orderId: `ORD-2024-${(index + 1).toString().padStart(3, '0')}`,
    medicine: medicines[Math.floor(Math.random() * medicines.length)],
    quantity: Math.floor(Math.random() * 1000) + 100,
    orderDate: new Date(2024, 0, Math.floor(Math.random() * 90) + 1).toISOString().split('T')[0],
    expectedDelivery: new Date(2024, 1, Math.floor(Math.random() * 90) + 1).toISOString().split('T')[0],
    status: statuses[Math.floor(Math.random() * statuses.length)],
    supplier: suppliers[Math.floor(Math.random() * suppliers.length)],
    amount: Math.floor(Math.random() * 5000) + 500,
    priority: Math.random() > 0.7 ? 'High' : Math.random() > 0.4 ? 'Medium' : 'Low',
    tracking: {
      current: 'In Transit',
      location: 'Distribution Center',
      updates: [
        { date: '2024-02-15', status: 'Order Placed', location: 'Supplier Warehouse' },
        { date: '2024-02-16', status: 'Processing', location: 'Packaging Center' },
        { date: '2024-02-18', status: 'Shipped', location: 'Distribution Hub' },
        { date: '2024-02-20', status: 'In Transit', location: 'Local Facility' }
      ]
    }
  }));
};

const generateHealthTrends = () => {
  return {
    localOutbreaks: [
      {
        disease: 'Seasonal Flu',
        intensity: 'High',
        affectedAreas: ['Urban Centers', 'Schools'],
        trend: 'Increasing',
        relatedMedicines: ['Oseltamivir', 'Antipyretics'],
        preventiveMeasures: ['Vaccination', 'Hand Hygiene']
      },
      {
        disease: 'Allergic Rhinitis',
        intensity: 'Moderate',
        affectedAreas: ['Suburban Areas', 'Parks'],
        trend: 'Stable',
        relatedMedicines: ['Antihistamines', 'Nasal Sprays'],
        preventiveMeasures: ['Air Purification', 'Pollen Avoidance']
      }
    ],
    weatherImpact: {
      temperature: '28°C',
      humidity: '65%',
      airQuality: 'Moderate',
      pollenCount: 'High',
      healthRisks: ['Respiratory Issues', 'Heat Stress']
    },
    demographicTrends: {
      ageGroups: {
        'Under 18': { commonConditions: ['Viral Infections', 'Allergies'] },
        '18-40': { commonConditions: ['Stress-related', 'Digestive Issues'] },
        '40-60': { commonConditions: ['Hypertension', 'Diabetes'] },
        'Over 60': { commonConditions: ['Arthritis', 'Cardiovascular'] }
      }
    }
  };
};

// OrderDetailModal Component
const OrderDetailModal = ({ order, onClose }) => {
  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={e => e.stopPropagation()}>
        <div className="modal-header">
          <h2>Order Details</h2>
          <button className="close-btn" onClick={onClose}>
            <FaTimes />
          </button>
        </div>
        <div className="modal-body">
          <div className="order-info">
            <h3>Order Information</h3>
            <div className="info-grid">
              <div className="info-item">
                <label>Order ID</label>
                <span>{order.orderId}</span>
              </div>
              <div className="info-item">
                <label>Medicine</label>
                <span>{order.medicine}</span>
              </div>
              <div className="info-item">
                <label>Quantity</label>
                <span>{order.quantity}</span>
              </div>
              <div className="info-item">
                <label>Amount</label>
                <span>${order.amount}</span>
              </div>
              <div className="info-item">
                <label>Supplier</label>
                <span>{order.supplier}</span>
              </div>
              <div className="info-item">
                <label>Status</label>
                <span className={`status-badge ${order.status.toLowerCase()}`}>
                  {order.status}
                </span>
              </div>
            </div>
          </div>
          
          <div className="tracking-timeline">
            <h3>Tracking Information</h3>
            <div className="timeline">
              {order.tracking.updates.map((update, index) => (
                <div key={index} className="timeline-item">
                  <div className="timeline-marker"></div>
                  <div className="timeline-content">
                    <h4>{update.status}</h4>
                    <p>{update.location}</p>
                    <span className="timeline-date">{update.date}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const Orders = () => {
  const [activeTab, setActiveTab] = useState('active');
  const [healthTrends, setHealthTrends] = useState(null);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [activeOrders, setActiveOrders] = useState([]);
  const [orderHistory, setOrderHistory] = useState([]);
  const [aiInsights, setAiInsights] = useState({});
  const [selectedTimeframe, setSelectedTimeframe] = useState('month');

  // AI Predictions and Insights
  const aiPredictions = [
    {
      id: 1,
      medicine: "Paracetamol 500mg",
      predictedDemand: 1500,
      confidence: 89,
      factors: [
        { type: "Seasonal", impact: "High", detail: "Monsoon season approaching - fever cases expected to rise" },
        { type: "Historical", impact: "Medium", detail: "20% increase in demand during similar period last year" },
        { type: "Environmental", impact: "High", detail: "Predicted rainfall in next 2 weeks" }
      ],
      recommendedAction: "Increase stock by 40%"
    },
    {
      id: 2,
      medicine: "Azithromycin 250mg",
      predictedDemand: 800,
      confidence: 92,
      factors: [
        { type: "Disease Outbreak", impact: "High", detail: "Recent respiratory infections in nearby areas" },
        { type: "Weather", impact: "Medium", detail: "Dropping temperatures increase risk of infections" },
        { type: "Population", impact: "Low", detail: "Increased patient registration in local clinics" }
      ],
      recommendedAction: "Place order for 1000 units"
    },
    // Add more AI predictions
  ];

  // Filter orders based on status
  const activeOrdersFiltered = activeOrders.filter(order => 
    ['Processing', 'Shipped', 'In Transit'].includes(order.status)
  );

  const orderHistoryFiltered = activeOrders.filter(order => 
    ['Delivered', 'Completed'].includes(order.status)
  );

  useEffect(() => {
    loadData();
  }, []);

  const loadData = () => {
    setHealthTrends(generateHealthTrends());
  };

  const refreshTrends = async () => {
    setIsRefreshing(true);
    await new Promise(resolve => setTimeout(resolve, 1500));
    setHealthTrends(generateHealthTrends());
    setIsRefreshing(false);
  };

  // Generate sample active orders
  useEffect(() => {
    const statuses = ['Processing', 'In Transit', 'Out for Delivery'];
    const medicines = [
      'Amoxicillin', 'Ibuprofen', 'Paracetamol', 'Omeprazole', 'Metformin',
      'Lisinopril', 'Amlodipine', 'Metoprolol', 'Simvastatin', 'Sertraline'
    ];
    const suppliers = ['PharmaCorp', 'MediSupply', 'HealthDirect', 'GlobalMeds', 'CareMeds'];

    const generateOrders = (count, isActive) => {
      return Array.from({ length: count }, (_, i) => {
        const today = new Date();
        const orderDate = new Date(today - Math.random() * 30 * 24 * 60 * 60 * 1000);
        const deliveryDays = Math.floor(Math.random() * 7) + 3;
        const expectedDelivery = new Date(orderDate.getTime() + deliveryDays * 24 * 60 * 60 * 1000);
        
        return {
          id: isActive ? `AO${i + 1}` : `HO${i + 1}`,
          medicine: medicines[Math.floor(Math.random() * medicines.length)],
          quantity: Math.floor(Math.random() * 1000) + 100,
          amount: Math.floor(Math.random() * 10000) + 1000,
          orderDate: orderDate.toISOString().split('T')[0],
          expectedDelivery: expectedDelivery.toISOString().split('T')[0],
          status: isActive ? 
            statuses[Math.floor(Math.random() * statuses.length)] : 
            'Delivered',
          supplier: suppliers[Math.floor(Math.random() * suppliers.length)],
          priority: Math.random() < 0.3 ? 'High' : Math.random() < 0.6 ? 'Medium' : 'Low',
          trackingNumber: `TRK${Math.random().toString(36).substr(2, 8).toUpperCase()}`
        };
      });
    };

    setActiveOrders(generateOrders(50, true));
    setOrderHistory(generateOrders(100, false));
  }, []);

  // Generate AI insights
  useEffect(() => {
    const generateAIInsights = () => {
      return {
        demandPredictions: {
          title: 'Demand Forecast Analysis',
          predictions: [
            {
              medicine: 'Amoxicillin',
              predictedDemand: Math.floor(Math.random() * 1000) + 500,
              confidence: 92.5,
              trend: 'increasing',
              factors: [
                { name: 'Seasonal Pattern', impact: 0.35 },
                { name: 'Historical Usage', impact: 0.45 },
                { name: 'Market Trends', impact: 0.20 }
              ]
            }
          ]
        },
        stockOptimization: {
          title: 'Inventory Optimization Insights',
          recommendations: [
            {
              type: 'Reorder Point Adjustment',
              details: 'Based on lead time analysis and demand variability',
              confidence: 88.7,
              impact: 'High',
              potentialSavings: '$12,500'
            }
          ]
        },
        anomalyDetection: {
          title: 'Supply Chain Anomaly Detection',
          findings: []
        },
        supplierAnalysis: {
          title: 'Supplier Performance Analytics',
          metrics: []
        }
      };
    };

    setAiInsights(generateAIInsights());
  }, [selectedTimeframe]);

  const renderOrderTable = (ordersList) => (
    <table className="orders-table">
      <thead>
        <tr>
          <th>Order ID</th>
          <th>Medicine</th>
          <th>Quantity</th>
          <th>Order Date</th>
          <th>Expected Delivery</th>
          <th>Status</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {ordersList.map(order => (
          <tr key={order.id}>
            <td>#{order.id}</td>
            <td>{order.medicine}</td>
            <td>{order.quantity}</td>
            <td>{order.orderDate}</td>
            <td>{order.expectedDelivery || 'N/A'}</td>
            <td>
              <span className={`status-badge ${order.status.toLowerCase()}`}>
                {order.status}
              </span>
            </td>
            <td>
              <button className="track-btn">Track Order</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );

  const renderAIInsights = () => (
    <div className="ai-insights-container">
      <div className="ai-header">
        <h2>AI-Powered Supply Chain Analytics</h2>
        <div className="timeframe-selector">
          <button 
            className={selectedTimeframe === 'week' ? 'active' : ''} 
            onClick={() => setSelectedTimeframe('week')}
          >
            Week
          </button>
          <button 
            className={selectedTimeframe === 'month' ? 'active' : ''} 
            onClick={() => setSelectedTimeframe('month')}
          >
            Month
          </button>
          <button 
            className={selectedTimeframe === 'quarter' ? 'active' : ''} 
            onClick={() => setSelectedTimeframe('quarter')}
          >
            Quarter
          </button>
        </div>
      </div>

      <div className="ai-insights-grid">
        <div className="insight-card demand-predictions">
          <h3><FaChartLine /> {aiInsights.demandPredictions?.title}</h3>
          {/* Render predictions */}
        </div>

        <div className="insight-card stock-optimization">
          <h3><FaBoxOpen /> {aiInsights.stockOptimization?.title}</h3>
          {/* Render recommendations */}
        </div>

        <div className="insight-card anomaly-detection">
          <h3><FaBrain /> {aiInsights.anomalyDetection?.title}</h3>
          {/* Render findings */}
        </div>

        <div className="insight-card supplier-analysis">
          <h3><FaTruckMoving /> {aiInsights.supplierAnalysis?.title}</h3>
          {/* Render metrics */}
        </div>
      </div>
    </div>
  );

  return (
    <div className="orders-page">
      <div className="orders-header">
        <h1>Orders Management</h1>
        <div className="tabs">
          <button 
            className={`tab ${activeTab === 'active' ? 'active' : ''}`}
            onClick={() => setActiveTab('active')}
          >
            Active Orders ({activeOrders.length})
          </button>
          <button 
            className={`tab ${activeTab === 'history' ? 'active' : ''}`}
            onClick={() => setActiveTab('history')}
          >
            Order History ({orderHistory.length})
          </button>
          <button 
            className={`tab ${activeTab === 'insights' ? 'active' : ''}`}
            onClick={() => setActiveTab('insights')}
          >
            AI Insights
          </button>
        </div>
      </div>

      <div className="orders-content">
        {activeTab === 'active' && (
          <div className="active-orders">
            <table>
              <thead>
                <tr>
                  <th>Order ID</th>
                  <th>Medicine</th>
                  <th>Quantity</th>
                  <th>Amount</th>
                  <th>Order Date</th>
                  <th>Expected Delivery</th>
                  <th>Status</th>
                  <th>Supplier</th>
                  <th>Priority</th>
                  <th>Tracking</th>
                </tr>
              </thead>
              <tbody>
                {activeOrders.map(order => (
                  <tr key={order.id}>
                    <td>{order.id}</td>
                    <td>{order.medicine}</td>
                    <td>{order.quantity}</td>
                    <td>${order.amount.toLocaleString()}</td>
                    <td>{order.orderDate}</td>
                    <td>{order.expectedDelivery}</td>
                    <td>
                      <span className={`status-badge ${order.status.toLowerCase().replace(' ', '-')}`}>
                        {order.status}
                      </span>
                    </td>
                    <td>{order.supplier}</td>
                    <td>
                      <span className={`priority-badge ${order.priority.toLowerCase()}`}>
                        {order.priority}
                      </span>
                    </td>
                    <td>{order.trackingNumber}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {activeTab === 'history' && (
          <div className="order-history">
            <table>
              <thead>
                <tr>
                  <th>Order ID</th>
                  <th>Medicine</th>
                  <th>Quantity</th>
                  <th>Amount</th>
                  <th>Order Date</th>
                  <th>Delivery Date</th>
                  <th>Status</th>
                  <th>Supplier</th>
                </tr>
              </thead>
              <tbody>
                {orderHistory.map(order => (
                  <tr key={order.id}>
                    <td>{order.id}</td>
                    <td>{order.medicine}</td>
                    <td>{order.quantity}</td>
                    <td>${order.amount.toLocaleString()}</td>
                    <td>{order.orderDate}</td>
                    <td>{order.expectedDelivery}</td>
                    <td>
                      <span className="status-badge delivered">
                        {order.status}
                      </span>
                    </td>
                    <td>{order.supplier}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {activeTab === 'insights' && renderAIInsights()}
      </div>

      {selectedOrder && (
        <OrderDetailModal 
          order={selectedOrder} 
          onClose={() => setSelectedOrder(null)}
        />
      )}
    </div>
  );
};

export default Orders;
