import React, { useState, useEffect } from 'react';
import { FaSync, FaBoxes, FaExclamationTriangle, FaHistory, FaBell, FaSearch, FaFilter, FaBoxOpen, FaChartLine, FaArrowUp, FaArrowDown, FaClock, FaBox, FaClipboardCheck, FaTruckMoving, FaExclamationCircle, FaDownload } from 'react-icons/fa';
import MedicineModal from '../components/MedicineModal';
import './Dashboard.css';

const Dashboard = () => {
  const [selectedMedicine, setSelectedMedicine] = useState(null);
  const [showUpdateStockModal, setShowUpdateStockModal] = useState(false);
  const [showOrderModal, setShowOrderModal] = useState(false);
  const [showHistoryModal, setShowHistoryModal] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedPeriod, setSelectedPeriod] = useState('today');
  const [isLoading, setIsLoading] = useState(false);
  const [activeFilter, setActiveFilter] = useState('all');
  const [showStockModal, setShowStockModal] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  const handleMedicineClick = (medicine) => {
    setSelectedMedicine(medicine);
  };

  const handleUpdateStock = (medicine) => {
    // Implement stock update logic
    console.log('Updating stock for:', medicine.name);
  };

  const handlePlaceOrder = (medicine) => {
    // Implement order placement logic
    console.log('Placing order for:', medicine.name);
  };

  const handleViewHistory = (medicine) => {
    // Implement history view logic
    console.log('Viewing history for:', medicine.name);
  };

  const [expiringMedicines] = useState([
    {
      id: 1,
      name: "Amoxicillin 500mg",
      category: "Antibiotics",
      currentStock: 150,
      daysUntilExpiry: 15,
      reorderPoint: 100,
      manufacturer: "PharmaCo Ltd",
      storageLocation: "Shelf A1",
      serialNumber: "AMX500-2024-001",
      expiryDate: "2024-04-01",
      status: "Critical",
      unit: "Tablets",
      tags: ["antibiotic", "prescription"]
    },
    {
      id: 2,
      name: "Paracetamol 650mg",
      category: "Pain Relief",
      currentStock: 300,
      daysUntilExpiry: 30,
      reorderPoint: 200,
      manufacturer: "MediCare Inc",
      storageLocation: "Shelf B2",
      serialNumber: "PCM650-2024-002",
      expiryDate: "2024-04-15",
      status: "Warning",
      unit: "Tablets",
      tags: ["analgesic", "otc"]
    },
    {
      id: 3,
      name: "Insulin Glargine",
      category: "Diabetes",
      currentStock: 50,
      daysUntilExpiry: 45,
      reorderPoint: 30,
      manufacturer: "DiabeCare",
      storageLocation: "Cold Storage 1",
      serialNumber: "INS-2024-003",
      expiryDate: "2024-05-01",
      status: "Warning",
      unit: "Vials",
      tags: ["insulin", "refrigerated"]
    },
    {
      id: 4,
      name: "Azithromycin 250mg",
      category: "Antibiotics",
      currentStock: 80,
      daysUntilExpiry: 20,
      reorderPoint: 60,
      manufacturer: "PharmaCo Ltd",
      storageLocation: "Shelf A2",
      serialNumber: "AZT250-2024-004",
      expiryDate: "2024-04-05",
      status: "Critical",
      unit: "Tablets",
      tags: ["antibiotic", "prescription"]
    },
    {
      id: 5,
      name: "Vitamin D3 Supplements",
      category: "Supplements",
      currentStock: 200,
      daysUntilExpiry: 40,
      reorderPoint: 150,
      manufacturer: "NutriLife",
      storageLocation: "Shelf C1",
      serialNumber: "VTD3-2024-005",
      expiryDate: "2024-04-25",
      status: "Warning",
      unit: "Tablets",
      tags: ["vitamin", "otc"]
    }
  ]);

  const [recentOrders] = useState([
    {
      id: "ORD-2024-001",
      medicine: "Amoxicillin 500mg",
      quantity: 500,
      orderDate: "2024-03-15",
      status: "Processing",
      supplier: "PharmaCo Ltd",
      amount: 2500
    },
    {
      id: "ORD-2024-002",
      medicine: "Insulin Glargine",
      quantity: 100,
      orderDate: "2024-03-14",
      status: "Shipped",
      supplier: "DiabeCare",
      amount: 5000
    },
    {
      id: "ORD-2024-003",
      medicine: "Paracetamol 650mg",
      quantity: 1000,
      orderDate: "2024-03-13",
      status: "Delivered",
      supplier: "MediCare Inc",
      amount: 1500
    }
  ]);

  const [healthTrends] = useState([
    {
      trend: "Seasonal Flu Outbreak",
      impact: "High demand for antipyretics and flu medication",
      recommendation: "Increase stock of Paracetamol and cold medicines",
      confidence: 85,
      timeframe: "Next 2 weeks"
    },
    {
      trend: "Rising Allergies",
      impact: "Increased need for antihistamines",
      recommendation: "Stock up on allergy medications",
      confidence: 78,
      timeframe: "Coming month"
    },
    {
      trend: "Monsoon Season Approaching",
      impact: "Higher risk of water-borne diseases",
      recommendation: "Prepare inventory of antibiotics and ORS",
      confidence: 92,
      timeframe: "Next 3 months"
    }
  ]);

  const supplies = [
    {
      id: 1,
      name: 'Analgesics',
      image: '/images/analgesics.png',
      category: 'Pain Relief'
    },
    {
      id: 2,
      name: 'Antibiotics',
      image: '/images/antibiotics.png',
      category: 'Infection'
    },
    {
      id: 3,
      name: 'Hypoglycemics',
      image: '/images/hypoglycemics.png',
      category: 'Diabetes'
    },
    {
      id: 4,
      name: 'Sedatives',
      image: '/images/sedatives.png',
      category: 'Sleep'
    },
    {
      id: 5,
      name: 'Tranquilizer',
      image: '/images/tranquilizer.png',
      category: 'Anxiety'
    },
    {
      id: 6,
      name: 'Vitamins',
      image: '/images/vitamins.png',
      category: 'Supplements'
    }
  ];

  // Sample data for the dashboard
  const stockSummary = {
    totalItems: {
      value: 2547,
      change: '+12%',
      trend: 'up'
    },
    lowStock: {
      value: 23,
      change: '-5%',
      trend: 'down'
    },
    expiringSoon: {
      value: 15,
      change: '+3%',
      trend: 'up'
    },
    outOfStock: {
      value: 7,
      change: '-2%',
      trend: 'down'
    }
  };

  const recentActivity = [
    { 
      id: 1, 
      type: 'New Order', 
      item: 'Amoxicillin', 
      quantity: 500, 
      date: '2024-02-20',
      status: 'Processing',
      icon: <FaClipboardCheck />
    },
    { 
      id: 2, 
      type: 'Stock Update', 
      item: 'Ibuprofen', 
      quantity: -50, 
      date: '2024-02-19',
      status: 'Completed',
      icon: <FaBox />
    },
    { 
      id: 3, 
      type: 'Delivery', 
      item: 'Metformin', 
      quantity: 1000, 
      date: '2024-02-18',
      status: 'In Transit',
      icon: <FaTruckMoving />
    },
    { 
      id: 4, 
      type: 'Expiry Alert', 
      item: 'Paracetamol', 
      quantity: 200, 
      date: '2024-02-17',
      status: 'Urgent',
      icon: <FaExclamationCircle />
    }
  ];

  const stockAlerts = [
    { 
      id: 1, 
      type: 'Low Stock', 
      item: 'Omeprazole', 
      current: 50, 
      threshold: 100,
      urgency: 'medium'
    },
    { 
      id: 2, 
      type: 'Expiring Soon', 
      item: 'Aspirin', 
      expiryDate: '2024-03-15',
      urgency: 'high'
    },
    { 
      id: 3, 
      type: 'Out of Stock', 
      item: 'Metoprolol',
      urgency: 'critical'
    },
    { 
      id: 4, 
      type: 'Low Stock', 
      item: 'Sertraline', 
      current: 30, 
      threshold: 75,
      urgency: 'medium'
    }
  ];

  const refreshData = async () => {
    setIsLoading(true);
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1000));
    setIsLoading(false);
  };

  const handleSearch = (e) => {
    setSearchTerm(e.target.value);
    // Filter activities and alerts based on search term
  };

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);
    // Apply filtering logic
  };

  const handlePeriodChange = (period) => {
    setSelectedPeriod(period);
    // Update data based on selected period
  };

  const handleExport = () => {
    // Export data logic
    console.log('Exporting data...');
  };

  const handleActionClick = (item) => {
    setSelectedItem(item);
    setShowStockModal(true);
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="header-left">
          <h1>Inventory Dashboard</h1>
          <div className="period-selector">
            {['today', 'week', 'month', 'year'].map(period => (
              <button 
                key={period}
                className={`period-btn ${selectedPeriod === period ? 'active' : ''}`}
                onClick={() => handlePeriodChange(period)}
              >
                {period.charAt(0).toUpperCase() + period.slice(1)}
              </button>
            ))}
          </div>
        </div>
        <div className="header-right">
          <div className="search-bar">
            <FaSearch />
            <input 
              type="text" 
              placeholder="Search inventory..."
              value={searchTerm}
              onChange={handleSearch}
            />
          </div>
          <button className="refresh-btn" onClick={refreshData} disabled={isLoading}>
            <FaSync className={isLoading ? 'spinning' : ''} />
            Refresh
          </button>
          <button className="export-btn" onClick={handleExport}>
            <FaDownload />
            Export
          </button>
        </div>
      </div>

      <div className="dashboard-stats">
        {Object.entries(stockSummary).map(([key, data]) => (
          <div key={key} className={`stat-card ${key}`}>
            <div className="stat-icon">
              {key === 'totalItems' && <FaBoxOpen />}
              {key === 'lowStock' && <FaExclamationTriangle />}
              {key === 'expiringSoon' && <FaClock />}
              {key === 'outOfStock' && <FaBox />}
            </div>
            <div className="stat-content">
              <h3>{key.replace(/([A-Z])/g, ' $1').replace(/^./, str => str.toUpperCase())}</h3>
              <div className="stat-value">
                <span className="number">{data.value}</span>
                <span className={`trend ${data.trend}`}>
                  {data.trend === 'up' ? <FaArrowUp /> : <FaArrowDown />}
                  {data.change}
                </span>
              </div>
            </div>
            <button className="view-details-btn">View Details</button>
          </div>
        ))}
      </div>

      <div className="dashboard-content">
        <div className="content-header">
          <div className="filter-buttons">
            {['all', 'orders', 'updates', 'alerts'].map(filter => (
              <button
                key={filter}
                className={`filter-btn ${activeFilter === filter ? 'active' : ''}`}
                onClick={() => handleFilterChange(filter)}
              >
                {filter.charAt(0).toUpperCase() + filter.slice(1)}
              </button>
            ))}
          </div>
        </div>

        <div className="content-grid">
          <div className="activity-section">
            <div className="section-header">
              <h2>Recent Activity</h2>
              <button className="view-all">View Full History</button>
            </div>
            <div className="activity-timeline">
              {recentActivity.map(activity => (
                <div key={activity.id} className="timeline-item">
                  <div className={`timeline-icon ${activity.type.toLowerCase().replace(' ', '-')}`}>
                    {activity.icon}
                  </div>
                  <div className="timeline-content">
                    <div className="timeline-header">
                      <h4>{activity.type}</h4>
                      <span className={`status ${activity.status.toLowerCase()}`}>
                        {activity.status}
                      </span>
                    </div>
                    <p className="item-name">{activity.item}</p>
                    <div className="timeline-meta">
                      <span className="quantity">Qty: {activity.quantity}</span>
                      <span className="date">{activity.date}</span>
                    </div>
                    <button 
                      className="action-btn"
                      onClick={() => handleActionClick(activity)}
                    >
                      Take Action
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>

          <div className="alerts-section">
            <div className="section-header">
              <h2>Critical Alerts</h2>
              <div className="alert-controls">
                <button className="filter-alerts">
                  <FaFilter />
                  Filter
                </button>
                <button className="view-all">View All Alerts</button>
              </div>
            </div>
            <div className="alerts-grid">
              {stockAlerts.map(alert => (
                <div key={alert.id} className={`alert-card ${alert.urgency}`}>
                  <div className="alert-header">
                    <span className={`urgency-badge ${alert.urgency}`}>
                      {alert.urgency.toUpperCase()}
                    </span>
                    <h4>{alert.type}</h4>
                  </div>
                  <div className="alert-body">
                    <p className="item-name">{alert.item}</p>
                    {alert.current && (
                      <div className="stock-meter">
                        <div className="meter-bar">
                          <div 
                            className="meter-fill" 
                            style={{width: `${(alert.current / alert.threshold) * 100}%`}}
                          />
                        </div>
                        <span className="meter-text">
                          {alert.current} / {alert.threshold} units
                        </span>
                      </div>
                    )}
                    {alert.expiryDate && (
                      <p className="expiry-date">Expires: {alert.expiryDate}</p>
                    )}
                  </div>
                  <div className="alert-actions">
                    <button 
                      className="resolve-btn"
                      onClick={() => handleActionClick(alert)}
                    >
                      Resolve Now
                    </button>
                    <button className="details-btn">View Details</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {showStockModal && (
        <div className="modal-overlay" onClick={() => setShowStockModal(false)}>
          <div className="modal-content" onClick={e => e.stopPropagation()}>
            <h3>Take Action</h3>
            <div className="modal-body">
              {/* Add action form or details here */}
              <p>Item: {selectedItem?.item}</p>
              <div className="action-buttons">
                <button className="update-stock">Update Stock</button>
                <button className="place-order">Place Order</button>
                <button className="cancel" onClick={() => setShowStockModal(false)}>
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Expiring Medicines Section */}
      <div className="section">
        <h2>Expiring Medicines</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Medicine Name</th>
                <th>Category</th>
                <th>Current Stock</th>
                <th>Days Until Expiry</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {expiringMedicines.map(medicine => (
                <tr key={medicine.id}>
                  <td onClick={() => handleMedicineClick(medicine)} style={{ cursor: 'pointer' }}>
                    {medicine.name}
                  </td>
                  <td>{medicine.category}</td>
                  <td>{medicine.currentStock} {medicine.unit}</td>
                  <td>{medicine.daysUntilExpiry} days</td>
                  <td>
                    <span className={`status-badge ${medicine.status.toLowerCase()}`}>
                      {medicine.status}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Recent Orders Section */}
      <div className="section">
        <h2>Recent Orders</h2>
        <div className="table-container">
          <table>
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Medicine</th>
                <th>Quantity</th>
                <th>Order Date</th>
                <th>Status</th>
                <th>Amount</th>
              </tr>
            </thead>
            <tbody>
              {recentOrders.map(order => (
                <tr key={order.id}>
                  <td>{order.id}</td>
                  <td>{order.medicine}</td>
                  <td>{order.quantity}</td>
                  <td>{order.orderDate}</td>
                  <td>
                    <span className={`status-badge ${order.status.toLowerCase()}`}>
                      {order.status}
                    </span>
                  </td>
                  <td>${order.amount}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Health Trends Section */}
      <div className="section">
        <h2>Real-Time Health Trends</h2>
        <div className="health-trends">
          {healthTrends.map((trend, index) => (
            <div key={index} className="trend-card">
              <h3>{trend.trend}</h3>
              <p><strong>Impact:</strong> {trend.impact}</p>
              <p><strong>Recommendation:</strong> {trend.recommendation}</p>
              <div className="confidence-meter">
                <div 
                  className="confidence-bar" 
                  style={{width: `${trend.confidence}%`}}
                ></div>
                <span>{trend.confidence}% Confidence</span>
              </div>
              <p className="timeframe">Expected within: {trend.timeframe}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Medicine Modal */}
      {selectedMedicine && (
        <MedicineModal
          medicine={selectedMedicine}
          onClose={() => setSelectedMedicine(null)}
          onUpdateStock={handleUpdateStock}
          onPlaceOrder={handlePlaceOrder}
          onViewHistory={handleViewHistory}
        />
      )}
    </div>
  );
};

export default Dashboard;
